const MessageDecorator: ClassDecorator = (target: Function)=>{
  target.prototype.message = (content: string)=>{
      console.log(content)
  }
}
@MessageDecorator
class LoginController {
  public login() {
      console.log('登陆业务处理');
      (this as any).message('恭喜你，登陆成功啦')
  }
}
new LoginController().login()

@MessageDecorator
class ArticleController {
  public store() {
      (this as any).message('文章添加成功啦')
  }
}

function TestDecorator() {
  return <div>TestDecorator</div>;
}

export default TestDecorator;
